<template>
  <v-main class="main">
    <div
      v-for="(knotch, i) in knotches"
      :key="'knotch-' + i"
      class="knotch"
      :class="knotchClasses[i]"
      @click="selectKnotch(i+1)"
    >
    </div>
  </v-main>
</template>
<style scoped>
@-webkit-keyframes glow {
    from {opacity: 1;}
    50% {opacity: 0.4;}
    to {opacity: 1;}
}
.main {
  background: #aaaaff;
  color: rgb(141, 0, 162);
  display: flex;
  padding: 1em !important;
}
.knotch {
  background: url('data:image/png;base64,');
  background-size: cover;
  display: inline-flex;
  width: 50px; 
  height: 50px;
}
.knotch.pending {
  opacity: 0.5;
}
</style>
<script>
export default {
  name: "Intro",
  components: {},
  data() {
    return {
      count: 0,
      knotches: [
        { name: "Hongos" },
        { name: "Grimm" },
        { name: "Coliseo" },
        { name: "Medusas" },
        { name: "Salubra 1" },
        { name: "Salubra 2" },
        { name: "Salubra 3" },
        { name: "Salubra 4" }
      ],
    };
  },
  computed: {
    knotchClasses: function () {
      const self = this;
      return self.knotches.map((knotch, i) => {
        let classes = [];
        if(self.count <= i) {
          classes.push("pending");
        }
        return classes.join(" ");
      });
    },
  },
  methods: {
    selectKnotch(i){
      const self = this;
      self.count = i;
    },
    _changeKnotch(movement) {
      const self = this;
      self.count = Math.max(Math.min(self.count + movement, self.knotches.length), 0);
    },
  },
  mounted() {
    const self = this;
    window.addEventListener('keydown', function(e) {
      switch(e.key){
        case "ArrowUp":
          self._changeKnotch(1);
          break;
        case "ArrowDown":
          self._changeKnotch(-1);
          break;
      }
    });
    document.title="Knotches";
  },
};
</script>
